export { default as c } from 'picocolors';
export { default as dayjs } from 'dayjs';

type Mode = 'dev' | 'prod';
type LogType = 'success' | 'warn' | 'error' | 'info' | 'debug';
type LogColor = 'green' | 'yellow' | 'red' | 'blue' | 'magenta' | 'cyan' | 'white';
type LogColorMap = Record<LogType, LogColor>;
type Time = string | boolean;
interface LoggerOptions {
    prefix: string;
    type: boolean;
    enable: boolean;
    mode: Mode;
    time: Time;
}
interface Logger {
    debug: (...args: any[]) => void;
    success: (...args: any[]) => void;
    warn: (...args: any[]) => void;
    error: (...args: any[]) => void;
    info: (...args: any[]) => void;
    log: (type: LogType, ...args: any[]) => void;
}

declare const createLogger: (options?: Partial<LoggerOptions>) => Logger;

declare function resolveOptions(options?: Partial<LoggerOptions>): LoggerOptions;

declare const _default: Logger;

export { LogColor, LogColorMap, LogType, Logger, LoggerOptions, Mode, Time, createLogger, _default as default, resolveOptions };
